#pythondef gsb(s, first, last):  #gsb = getStringBetween	start = s.index(first) + len(first);	end = s.index(last, start);	return s[start:end];#end deffile1 = open("data1.tmf");data1 = file1.read();data1 = data1.replace('\n', '').replace('\t', '').replace('	', '');data1_tmf = gsb(data1, "tmf:", ":tmf");data1_tmf_pes1 = gsb(data1_tmf, "pes1:", ":pes1");data1_tmf_pes1_meno = gsb(data1_tmf_pes1, "meno:", ":meno");data1_tmf_pes1_vek = gsb(data1_tmf_pes1, "vek:", ":vek");data1_tmf_otec = gsb(data1_tmf, "otec:", ":otec");data1_tmf_otec_meno = gsb(data1_tmf_otec, "meno:", ":meno");data1_tmf_otec_vek = gsb(data1_tmf_otec, "vek:", ":vek");print("V rodine je pes. Vola sa %s. Ma %s rokov." % (data1_tmf_pes1_meno, data1_tmf_pes1_vek));print("V rodine je tiez otec. Vola sa %s. Je %s rokov stary." % (data1_tmf_otec_meno, data1_tmf_otec_vek));	